  `pet_friendly` tinyint(1) DEFAULT 0,
  `family_friendly` tinyint(1) DEFAULT 0,
  `romantic` tinyint(1) DEFAULT 0,
  `couple_friendly` tinyint(1) DEFAULT 0,
  `near_water` tinyint(1) DEFAULT 0,
  `bbq_area` tinyint(1) DEFAULT 0,
